const favClick = id => {
  const storedFavs = JSON.parse(localStorage.getItem("favs")) || [];
  storedFavs.push(id);
  localStorage.setItem("favs", JSON.stringify(storedFavs));
  alert("favourited");
};

const List = ({ films, clickHandler, favs }) => {
  const list = films.map(film => {
    if (favs.indexOf(film.episode_id) >= 0) {
      return (
        <li key={film.episode_id} className="fav">
          {film.title} - unFav
        </li>
      );
    } else {
      return (
        <li key={film.episode_id} className="nonfav">
          {film.title} -{" "}
          <button
            onClick={() => {
              clickHandler(film.episode_id);
            }}
          >
            Fav
          </button>
        </li>
      );
    }
  });
  return list;
};

export default List;
