import React, { useState, useEffect } from "react";
import fetch from "isomorphic-unfetch";
import Search from "../components/search";

const IndexPage = props => {
  const [films, setFilms] = useState(props[0]);
  const [favs, setFavs] = useState([]);
  console.log(films);
  let htmlList = films.map(film => (
    <li key={film.episode_id}>{film.title} - Fav</li>
  ));

  useEffect(() => {
    // when you add new fav, you want this to run again
    // setfavs outside of useEffect
    // change the htmlList inside here
    // page will rerender after useEffect
    const StoredFavs = JSON.parse(localStorage.getItem("favs"));
    if (StoredFavs) {
      const list = films.map(film => {
        if (favs.indexOf(film.episode_id) > 0) {
          <li key={film.episode_id} className="fav">
            {film.title} - unFav
          </li>;
        } else {
          <li key={film.episode_id} className="nonfav">
            {film.title} - Fav
          </li>;
        }
      });
      console.log(list);
    }
    htmlList = <li>Test</li>;
    // setFavs(["test"]);
    console.log("useEffect run");
  }, [favs]);

  return (
    <div>
      <Search handleSearch={filmList => setFilms(filmList)} />
      <h1>Star Wars Films</h1>
      <ul>{htmlList}</ul>
      <button onClick={() => setFilms([])}>Click</button>
    </div>
  );
};

IndexPage.getInitialProps = async () => {
  const url = "https://swapi.co/api/films/";
  const res = await fetch(url);
  const data = await res.json();

  console.log("run");
  return [data.results];
};

export default IndexPage;
