import React, { useState, useEffect } from "react";
import fetch from "isomorphic-unfetch";
import Search from "../components/search";

const createList = (films, favs = []) => {
  const list = films.map(film => {
    if (favs.indexOf(film.episode_id) > 0) {
      return (
        <li key={film.episode_id} className="fav">
          {film.title} - unFav
        </li>
      );
    } else {
      return (
        <li key={film.episode_id} className="nonfav">
          {film.title} - Fav
        </li>
      );
    }
  });
  return list;
};

const IndexPage = props => {
  const [films, setFilms] = useState(props[0]);
  const [favs, setFavs] = useState([]);
  const [html, setHtml] = useState(createList(films));
  const [toggle, setToggle] = useState(false);
  console.log(films);
  console.log(html);

  useEffect(() => {
    const storedFavs = JSON.parse(localStorage.getItem("favs"));
    setFavs(storedFavs || []);
    setHtml(createList(films, favs));

    console.log("useEffect run");
  }, [toggle]);

  return (
    <div>
      <Search handleSearch={filmList => setFilms(filmList)} />
      <h1>Star Wars Films</h1>
      <ul>{html}</ul>
      <button onClick={() => setHtml(<li>test</li>)}>Fav</button>
    </div>
  );
};

// onclick of fave, will push to local storage, will update fav state

IndexPage.getInitialProps = async () => {
  const url = "https://swapi.co/api/films/";
  const res = await fetch(url);
  const data = await res.json();

  console.log("run");
  return [data.results];
};

export default IndexPage;
