import React, { useState, useEffect } from "react";
import fetch from "isomorphic-unfetch";
import Search from "../components/search";

const favClick = favs => {
  const storedFavs = JSON.parse(localStorage.getItem("favs")) || [];
  storedFavs.push;
  localStorage.setItem("favs", JSON.stringify(favs));
  alert("favourited");
};

const createList = (films, clickHandler = null, favs = []) => {
  const list = films.map(film => {
    if (favs.indexOf(film.episode_id) > 0) {
      return (
        <li key={film.episode_id} className="fav">
          {film.title} - unFav
        </li>
      );
    } else {
      return (
        <li key={film.episode_id} className="nonfav">
          {film.title} -{" "}
          <button
            onClick={() => {
              clickHandler(film.episode_id);
            }}
          >
            Fav
          </button>
        </li>
      );
    }
  });
  return list;
};

const IndexPage = props => {
  const [films, setFilms] = useState(props[0]);
  const [favs, setFavs] = useState([]);
  const [html, setHtml] = useState(createList(films));
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    const storedFavs = JSON.parse(localStorage.getItem("favs"));
    setFavs(storedFavs || []);
    setHtml(createList(films, favClick, favs));

    console.log("useEffect run");
  }, [toggle]);

  return (
    <div>
      <Search handleSearch={filmList => setFilms(filmList)} />
      <h1>Star Wars Films</h1>
      <ul>{html}</ul>
    </div>
  );
};

// onclick of fave, will push to local storage, will update fav state

IndexPage.getInitialProps = async () => {
  const url = "https://swapi.co/api/films/";
  const res = await fetch(url);
  const data = await res.json();

  console.log("run");
  return [data.results];
};

export default IndexPage;
