const List = ({ films, clickHandler = null, favs = [] }) => {
  console.log("list", favs);
  const list = films.map(film => {
    if (favs.indexOf(film.episode_id) > 0) {
      console.log("true", film);
      return (
        <li key={film.episode_id} className="fav">
          {film.title} - unFav
        </li>
      );
    } else {
      console.log("false", favs.indexOf(film.episode_id));
      return (
        <li key={film.episode_id} className="nonfav">
          {film.title} -{" "}
          <button
            onClick={() => {
              clickHandler(film.episode_id);
            }}
          >
            Fav
          </button>
        </li>
      );
    }
  });
  return list;
};

export default List;
